import React from "react";
import { BrowserRouter, Routes, Route, Link, NavLink, Navigate, useNavigate, useSearchParams } from "react-router-dom";
import netlifyIdentity from "netlify-identity-widget";

// identity helper
const useIdentity = () => {
  const [user, setUser] = React.useState(null);
  React.useEffect(() => {
    netlifyIdentity.init();
    const u = netlifyIdentity.currentUser();
    setUser(u);
    netlifyIdentity.on("login", (u) => setUser(u));
    netlifyIdentity.on("logout", () => setUser(null));
    return () => { netlifyIdentity.off("login"); netlifyIdentity.off("logout"); };
  }, []);
  const roles = (user && user.app_metadata && user.app_metadata.roles) || [];
  return {
    user,
    isAdmin: roles.includes("admin"),
    isPaid: roles.includes("member") || roles.includes("vip") || roles.includes("admin")
  };
};

const Badge = ({ children, tone="brand" }) => (
