// content loader (Webpack require.context in CRA)
const SafeContent = ({folder, render}) => {
  const [items, setItems] = React.useState([]);
  React.useEffect(() => {
    try {
      // @ts-ignore
      const req = require.context(`./content/${folder}`, false, /\.json$/);
      setItems(req.keys().map(k => req(k)));
    } catch {
      setItems([]);
    }
  }, [folder]);
  return render(items);
};

