const AdminDashboard = () => {
  const [rows, setRows] = React.useState([]);
  const load = async () => {
    const user = netlifyIdentity.currentUser();
    const token = user ? await user.jwt() : null;
    const res = await fetch("/.netlify/functions/admin-paymentsList", {
      headers: token ? { Authorization: `Bearer ${token}` } : {}
    });
    const data = await res.json();
    setRows(data.items || []);
  };
  React.useEffect(() => { load(); }, []);
  const markPaid = async (orderId) => {
    const token = netlifyIdentity.currentUser() ? await netlifyIdentity.currentUser().jwt() : null;
    await fetch("/.netlify/functions/admin-markPaid", {
      method: "POST",
      headers: { "Content-Type": "application/json", ...(token ? { Authorization: `Bearer ${token}` } : {}) },
      body: JSON.stringify({ orderId })
    });
    load();
  };
  return (
    <div className="max-w-7xl mx-auto px-4 py-12">
      <h2 className="text-3xl font-extrabold text-brand-700 mb-6">Admin Dashboard</h2>
      <div className="mb-4 flex gap-3">
        <a className="px-4 py-2 rounded-xl bg-gray-200" href="/admin">Open CMS</a>
        <button className="px-4 py-2 rounded-xl bg-gray-200" onClick={load}>Refresh</button>
      </div>
      <div className="overflow-x-auto glass rounded-2xl border border-pink-100">
        <table className="w-full text-left text-sm">
          <thead className="bg-pink-50 text-gray-700">
            <tr>
              <th className="p-3">Order ID</th>
              <th className="p-3">Plan</th>
              <th className="p-3">Amount</th>
              <th className="p-3">Status</th>
              <th className="p-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {rows.map(r => (
              <tr key={r.orderId} className="border-t border-pink-100">
                <td className="p-3">{r.orderId}</td>
                <td className="p-3">{r.plan}</td>
                <td className="p-3">{r.amountINR}</td>
                <td className="p-3">{r.status}</td>
                <td className="p-3">
                  {r.status !== "paid" && <button className="px-3 py-1 rounded bg-brand-500 text-white" onClick={() => markPaid(r.orderId)}>Mark Paid</button>}
                </td>
              </tr>
            ))}
            {rows.length === 0 && <tr><td className="p-3 text-gray-500" colSpan="5">No payments yet.</td></tr>}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Checkout
const Checkout = () => {
  const [search] = useSearchParams();
  const plan = search.get("plan") || "basic";
  const amountParam = Number(search.get("amount") || 800);
  const [order, setOrder] = React.useState(null);
  const [status, setStatus] = React.useState(null);
  const [error, setError] = React.useState(null);
  const navigate = useNavigate();
  const createOrder = async () => {
    setError(null);
    const res = await fetch("/.netlify/functions/payments-createOrder", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ plan, amountINR: amountParam })
    });
    const data = await res.json();
    if (!res.ok) { setError(data.error || "Failed to create order"); return; }
    setOrder(data);
    pollStatus(data.orderId);
  };
  const pollStatus = async (orderId) => {
    const loop = async () => {
      const r = await fetch(`/.netlify/functions/payments-getStatus?orderId=${encodeURIComponent(orderId)}`);
      const s = await r.json();
      setStatus(s);
      if (s.status === "paid") {
        navigate(`/success?orderId=${encodeURIComponent(orderId)}`);
      } else setTimeout(loop, 5000);
    };
    loop();
  };
  return (
    <div className="max-w-2xl mx-auto p-8">
      <h2 className="text-3xl font-extrabold text-brand-700 mb-4">Checkout</h2>
      <p className="text-gray-600 mb-6">Plan: <b className="uppercase">{plan}</b>  Amount: <b>{amountParam}</b></p>
      {!order ? (
        <button className="px-5 py-3 rounded-xl bg-brand-500 text-white shadow" onClick={createOrder}>Generate UPI Link & QR</button>
      ) : (
        <div className="space-y-4">
          <div className="glass p-4 rounded-xl">
            <p className="text-gray-700">UPI Link:</p>
            <a className="underline break-all text-brand-700" href={order.upiLink}> {order.upiLink}</a>
          </div>
          <div className="glass p-4 rounded-xl">
            <p className="text-gray-700 mb-2">Scan to pay:</p>
            <img className="bg-white p-2 rounded" alt="UPI QR" src={`https://api.qrserver.com/v1/create-qr-code/?size=320x320&data=${encodeURIComponent(order.upiLink)}`} />
          </div>
          <div className="text-gray-500 text-sm">
            <p>After paying, this page will auto-detect once your payment is confirmed.</p>
          </div>
        </div>
      )}
      {error && <p className="text-red-500 mt-4">{error}</p>}
      {status && status.status !== "paid" && <p className="text-yellow-700 mt-4">Current status: {status.status}</p>}
    </div>
  );
};

